package com.tecnara.manoamiga.expo.controllers;

import com.tecnara.manoamiga.aaa.controllers.TecnaraBaseController;
import com.tecnara.manoamiga.expo.entities.ImagenEntity;
import com.tecnara.manoamiga.expo.repositories.IImagenRepository;
import java.io.UnsupportedEncodingException;
import java.util.Base64;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.MediaType;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.ResponseBody;

@Controller
@RequestMapping("/expo/coordinador/imagen")
public class CoordinadorImagenController extends TecnaraBaseController {

    @Autowired
    private IImagenRepository repoImagen;

    @GetMapping(value = "/descargar",
            produces = MediaType.IMAGE_JPEG_VALUE)
    public @ResponseBody
    byte[] descargar(Long id) {
        ImagenEntity img = repoImagen.findById(id).get();
        byte[] datos = img.getImagen();
        return datos;
    }

    @GetMapping(value = "/base64")
    public @ResponseBody
    String base64(Long id) {
        ImagenEntity img = repoImagen.findById(id).get();
        byte[] encoded = Base64.getEncoder().encode(img.getImagen());
        return new String(encoded);
    }

    @GetMapping(value = "/count")
    public @ResponseBody
    Long lista() {
        return repoImagen.count();
    }

    @GetMapping(value = "/prueba",
            produces = MediaType.IMAGE_JPEG_VALUE)
    public @ResponseBody
    byte[] prueba() {
        String a="";
        byte b[]=convertToImage(a); 
        byte c[]=Base64.getEncoder().encode(b);
        String s=new String(c);
        System.out.println(s);
        return convertToImage("");
    }

    public byte[] convertToImage(String base64) {
        try {
            byte[] decodedString = Base64.getDecoder().decode(base64.getBytes("UTF-8"));
            return decodedString;
        } catch (UnsupportedEncodingException e) {
            // TODO Auto-generated catch block
            e.printStackTrace();
            return null;
        }
    }

}
